import React from "react";
import "./footer.css";

import { withStyles } from "@material-ui/core/styles";
import FormControlLabel from "@material-ui/core/FormControlLabel";

const IOSSwitch = withStyles(theme => ({
  root: {
    width: 42,
    height: 26,
    padding: 0,
    margin: theme.spacing(1)
  },
  switchBase: {
    padding: 1,
    "&$checked": {
      transform: "translateX(16px)",
      color: theme.palette.common.white,
      "& + $track": {
        backgroundColor: "#52d869",
        opacity: 1,
        border: "none"
      }
    },
    "&$focusVisible $thumb": {
      color: "#52d869",
      border: "6px solid #fff"
    }
  },
  thumb: {
    width: 24,
    height: 24
  },
  track: {
    borderRadius: 26 / 2,
    border: `1px solid ${theme.palette.grey[400]}`,
    backgroundColor: theme.palette.grey[50],
    opacity: 1,
    transition: theme.transitions.create(["background-color", "border"])
  },
  checked: {},
  focusVisible: {}
}));

const Component = info => {
  console.log(info);
  const handleChange = () => {
    info.setTheme(!info.darkmode);
  };
  return (
    <div className="footer-outer-div">
      <div
        className="footer-div"
        style={
          window.innerWidth < 960
            ? { flexDirection: "column" }
            : { flexDirection: "row" }
        }
      >
        <ul>
          <li>
            <h2>Support</h2>
          </li>
          <li>
            <a id="copy" href="#copy">
              <h4
                onClick={() => {
                  try {
                    navigator.clipboard.writeText(
                      "13AcjHWeFJA6VucLXpsFfTLpYtDkksRhaY"
                    );
                  } catch (e) {}
                }}
              >
                Bitcoin Wallet
              </h4>
            </a>
          </li>
          <li>
            <a id="copy" href="#copy">
              <h4
                onClick={() => {
                  try {
                    navigator.clipboard.writeText(
                      "0x7f92601df8649afe42eed70c887e52c82f9c179a"
                    );
                  } catch (e) {}
                }}
              >
                Ethereum Wallet
              </h4>
            </a>
          </li>
          <li>
            <a id="copy" href="#copy">
              <h4
                onClick={() => {
                  try {
                    navigator.clipboard.writeText(
                      "bnb136ns6lfw4zs5hg4n85vdthaad7hq5m4gtkgf23"
                    );
                  } catch (e) {}
                }}
              >
                Binance Coin Wallet
              </h4>
            </a>
          </li>
        </ul>
        <ul>
          <li>
            <h2>Contact</h2>
          </li>
          <li>
            <a id="copy" href="mailto:whyiscoin@gmail.com">
              <h4>whyiscoin@gmail.com</h4>
            </a>
          </li>
          <li>
            <h2>Theme</h2>{" "}
            <FormControlLabel
              control={
                <IOSSwitch
                  checked={info.darkmode}
                  onChange={handleChange}
                  name="darkmode"
                />
              }
              label="ternary"
            />
          </li>
        </ul>
        <h2 style={{ maxWidth: "70vw", textAlign: "center" }}>
          Reimagine the future of researching investments.
        </h2>
      </div>
      <h4>Â© 2021 by Why is Coin?.</h4>
    </div>
  );
};

export default Component;
