import React, { useState, useEffect } from "react";
import "./ticker-news.css";

import Twitter from "./twitter.jsx";
import News from "./news.jsx";
import Transactions from "./transactions.jsx";

const Component = info => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchNews = async () => {
    let res = await new XMLHttpRequest();
    res.onload = apiSucc;
    res.onerror = console.log();
    res.open(
      "GET",
      "https://why-is-coin-api.herokuapp.com/rtdata/news?keyword=" +
        info.data[1] +
        "&delta=" +
        info.data[0] / 100
    );
    res.send();
  };

  function apiSucc() {
    try {
      setData(JSON.parse(this.responseText));
      setLoading(false);
    } catch (e) {
      setLoading(false);
      console.log(e);
    }
  }

  useEffect(() => {
    fetchNews();
    // eslint-disable-next-line
  }, []);

  return (
    <div
      className="news-parent-div"
      style={
        window.innerWidth < 960
          ? {
              width: "100%",
              backgroundColor: info.colors[0],
              color: info.colors[2],
              marginTop: "-15px"
            }
          : {
              width: "75vw",
              display: "inline-block",
              backgroundColor: info.colors[0],
              color: info.colors[2]
            }
      }
    >
      <div className="news-div-block">
        <div id="news-div-top">
          <h3>
            News{" "}
            <span
              style={{
                float: "right",
                margin: 0,
                marginTop: "4px",
                marginRight: "5px",
                fontSize: "14px"
              }}
            >
              {" "}
              Total Sentiment:ðŸ˜€{" "}
              {data.news !== undefined
                ? data.news.sentiment === 0
                  ? "Neutral"
                  : Math.floor(data.news.sentiment * 100) / 100 + "%"
                : ""}
            </span>
          </h3>
        </div>
        <div className="news-div-content">
          {loading ? (
            <React.Fragment>Loading..</React.Fragment>
          ) : (
            <News articles={data.news} colors={info.colors} />
          )}
        </div>
      </div>
      <div
        style={
          window.innerWidth < 960
            ? {
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                width: "100%",
                flexDirection: "column"
              }
            : {
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                width: "100%",
                flexDirection: "row"
              }
        }
      >
        <div
          className="news-div-block"
          style={
            window.innerWidth < 960
              ? { width: "90%" }
              : { width: "45%", maxWidth: "600px" }
          }
        >
          <div id="news-div-top">
            <h3 style={{ margin: 0 }}>
              Twitter{" "}
              <span
                style={{
                  float: "right",
                  margin: 0,
                  marginTop: "4px",
                  marginRight: "5px",
                  fontSize: "14px"
                }}
              >
                {" "}
                Total Sentiment:{" "}
                {data.twitter !== undefined
                  ? data.twitter.sentiment === 0
                    ? "Neutral"
                    : Math.floor(data.twitter.sentiment * 100) / 100 + "%"
                  : ""}
              </span>
            </h3>
          </div>
          <div className="news-div-content">
            {loading ? (
              <React.Fragment>Loading..</React.Fragment>
            ) : (
              <Twitter tweets={data.twitter} colors={info.colors} />
            )}
          </div>
        </div>
        <div
          className="news-div-block"
          style={window.innerWidth < 960 ? { width: "90%" } : { width: "45%" }}
        >
          <div id="news-div-top">
            <h3>Whales</h3>
          </div>
          <div className="news-div-content">
            <Transactions colors={info.colors} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Component;
